import BaseComponent from "ui/component/component";

export default class Image extends BaseComponent 
{
    public static template() {
        return `
            <span class="d-flex flex-column align-items-center {{class}}">
                <img class="img-thumbnail p-0 border-0" src="{{src}}" alt="{{alt}}">
                {{#if cap}}
                <small>{{cap}}</small>
                {{/if}}
            </span>
        `;
    }

    public static properties() {
        let props = super.properties();

        props["src"] = {
            label: "Src",
            type: "mmTextfield"
        };

        props["alt"] = {
            label: "Alt",
            type: "mmTextfield"
        };

        props["cap"] = {
            label: "Caption",
            type: "mmTextfield"
        };

        return props;
    }
}