import BaseComponent from "ui/component/component";

export default class Label extends BaseComponent 
{
    public static template() {
        return `
            <span class="{{class}}">
                {{label}}
            </span>
        `;
    }

    public static properties() {
        let props = super.properties();

        props["label"] = {
            label: "Text",
            type: "mmTextfield"
        };

        return props;
    }
}